mapper.update:
UPDATE card SET name=#{cardCustom.name}, occupation_list=#{cardCustom.occupation_list},
				email_list=#{cardCustom.email_list}, phone_list=#{cardCustom.phone_list},
				address_list=#{cardCustom.address_list}
WHERE id=#{cardCustom.id};

mapper.select:
SELECT *
FROM card
WHERE id = #{cardCustom.id} AND username = #{cardCustom.username}
